import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import UserService from '../service/UserService';

function UpdateUser() {
    const navigate = useNavigate();
    const { userId } = useParams();


    const [userData, setUserData] = useState({
        name: '',
        email: '',
        city: ''
    });

    useEffect(() => {
        fetchUserDataById(userId); // Pass the userId to fetchUserDataById
    }, [userId]); //wheen ever there is a chane in userId, run this

    const fetchUserDataById = async (userId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await UserService.getUserByIdForUser(userId, token); // Pass userId to getUserById
            const { name, email, city } = response.ourUsers;
            setUserData({ name, email, city });
        } catch (error) {
            console.error('Error fetching user data:', error);
        }
    };


    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserData((prevUserData) => ({
            ...prevUserData,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const confirmDelete = window.confirm('Are you sure you want to update this user?');
            if (confirmDelete) {
                const token = localStorage.getItem('token');
                const res = await UserService.updateUserForUser(userId, userData, token);
                console.log(res)
                // Redirect to profile page or display a success message
                navigate("/profile")
            }

        } catch (error) {
            console.error('Error updating user profile:', error);
            alert(error)
        }
    };

    return (
        <div className="auth-container">
            <h2>Update User</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Name:</label>
                    <input type="text" name="name" value={userData.name} onChange={handleInputChange} />
                </div>
                <div className="form-group">
                    <label>Email:</label>
                    <input type="email" name="email" value={userData.email} onChange={handleInputChange} />
                </div>

                <div className="form-group">
                    <label>City:</label>
                    <input type="text" name="city" value={userData.city} onChange={handleInputChange} />
                </div>
                <button type="submit">Update</button>
            </form>
        </div>
    );
}

export default UpdateUser;
