import React, { useState } from "react";
import EventService from "./components/service/EventService";

const AddEventPage = () => {
    const [event, setEvent] = useState({
        name: "",
        description: "",
        dateTime: "",
        location: "",
        availableTickets: "",
    });
    const [message, setMessage] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEvent({ ...event, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem("jwtToken");
        try {
            const response = await EventService.addEvent(event, token);
            setMessage(`Event added successfully: ${response.message}`);
            setEvent({
                name: "",
                description: "",
                dateTime: "",
                location: "",
                availableTickets: "",
            });
        } catch (error) {
            setMessage(
                error.response?.data?.message ||
                "An error occurred while adding the event."
            );
        }
    };

    return (
        <div style={{ maxWidth: "600px", margin: "50px auto" }}>
            <h1>Add Event</h1>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: "15px" }}>
                    <label htmlFor="name">Event Name:</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={event.name}
                        onChange={handleChange}
                        required
                        style={{ width: "100%", padding: "8px", marginTop: "5px" }}
                    />
                </div>
                <div style={{ marginBottom: "15px" }}>
                    <label htmlFor="description">Description:</label>
                    <textarea
                        id="description"
                        name="description"
                        value={event.description}
                        onChange={handleChange}
                        required
                        style={{ width: "100%", padding: "8px", marginTop: "5px" }}
                    />
                </div>
                <div style={{ marginBottom: "15px" }}>
                    <label htmlFor="dateTime">Date and Time:</label>
                    <input
                        type="datetime-local"
                        id="dateTime"
                        name="dateTime"
                        value={event.dateTime}
                        onChange={handleChange}
                        required
                        style={{ width: "100%", padding: "8px", marginTop: "5px" }}
                    />
                </div>
                <div style={{ marginBottom: "15px" }}>
                    <label htmlFor="location">Location:</label>
                    <input
                        type="text"
                        id="location"
                        name="location"
                        value={event.location}
                        onChange={handleChange}
                        required
                        style={{ width: "100%", padding: "8px", marginTop: "5px" }}
                    />
                </div>
                <div style={{ marginBottom: "15px" }}>
                    <label htmlFor="availableTickets">Available Tickets:</label>
                    <input
                        type="number"
                        id="availableTickets"
                        name="availableTickets"
                        value={event.availableTickets}
                        onChange={handleChange}
                        required
                        style={{ width: "100%", padding: "8px", marginTop: "5px" }}
                    />
                </div>
                <button type="submit" style={{ padding: "10px 20px", cursor: "pointer" }}>
                    Add Event
                </button>
            </form>
            {message && <p style={{ marginTop: "20px" }}>{message}</p>}
        </div>
    );
};

export default AddEventPage;
