import React, { useState, useEffect } from 'react';
import EventService from '../service/EventService';
import './Tickets.css'

function TicketCartPage() {
    const [tickets, setTickets] = useState([]);

    useEffect(() => {
        fetchTickets();
    }, []);

    const fetchTickets = async () => {
        try {
            const token = localStorage.getItem('token');
            const username = localStorage.getItem('username'); // Presupunând că salvezi username-ul local
            const response = await EventService.getMyTickets(username, token);
            setTickets(response);
        } catch (error) {
            console.error('Error fetching tickets:', error);
        }
    };

    return (
        <div className="ticket-cart-container">
            <h2>My Tickets</h2>
            {tickets.length > 0 ? (
                <ul>
                    {tickets.map((ticket) => (
                        <li key={ticket.id}>
                            <h3>{ticket.event.name}</h3>
                            <p><strong>Date:</strong> {ticket.event.dateTime}</p>
                            <p><strong>Location:</strong> {ticket.event.location}</p>
                            <p><strong>Description:</strong> {ticket.event.description}</p>
                            <p><strong>Purchased on:</strong> {ticket.purchaseDate}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No tickets purchased yet.</p>
            )}
        </div>
    );
}

export default TicketCartPage;
